
exports.rand_two_numbers = function()
{
	return Math.floor( Math.random() * 100 );
};

exports.rand_two_numbers_max = function( nMax )
{
	return Math.floor( Math.random() * 100 ) % nMax;
};

exports.rand_two_numbers_min_max = function( nMin, nMax )
{
	var nRand = Math.floor( Math.random() * 100 ) % nMax;
	
	if( nRand < nMin )
		nRand += nMin;
	
	if( nRand > nMax )
		nRand %= nMax;
		
	return nRand ;
};

